import { HttpClient, HttpHeaders  } from '@angular/common/http';
import { Injectable } from '@angular/core';
// import 'rxjs/add/operator/map';
// import 'rxjs/add/operator/toPromise';
import { CustomerInvoices } from '../../shared/customer-invoices.model';
@Injectable({
  providedIn: 'root'
})

export class CustomerInvoicesService {

  selectedCustomerInvoices: any;
  isUpdate:boolean=false;
  customerInvoicesList:CustomerInvoices | undefined;
  constructor(private http:HttpClient) { }

  getcustomerInvoicesList()
  {
    return this.http.get("https://localhost:44352/api/customerInvoices");
  }
  PostCustomer_Invoices(r: CustomerInvoices)
  {
    return this.http.post<CustomerInvoices>("https://localhost:44352/api/customerInvoices",r);
  }
  //  PutCustomer_Invoices(Id: string, customerI: CustomerInvoices) {
  //   return this.http.put<CustomerInvoices>("https://localhost:44352/api/customerInvoices",{Id,customerI} );
  // }
  PutCustomer_Invoices(id :string, Cust: CustomerInvoices) {
    console.log("put func",this.selectedCustomerInvoices)
    return this.http.put<CustomerInvoices>("https://localhost:44352/api/customerInvoices",{id:this.customerInvoicesList?.InvId, Cust});
  }

  DeleteCustomer_Invoices(Id: string) {
    return this.http.delete("https://localhost:44352/api/customerInvoices"+Id);
  }
}
