import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { CustomerInvoicesService } from '../../shared/customer-invoices.service'
import { ToastrService } from 'ngx-toastr'
@Component({
  selector: 'app-customer-invoices',
  templateUrl: './customer-invoices.component.html',
  styleUrls: ['./customer-invoices.component.css'],
})
export class CustomerInvoicesComponent implements OnInit{

  constructor(public customerInvSer:CustomerInvoicesService, private toaster: ToastrService){ }

  ngOnInit() {
    this.resetForm();
  }
  resetForm(form?: NgForm){
    if(form!=null)
      form.reset();
    this.customerInvSer.selectedCustomerInvoices={
    InvId: '',
    InvDate:'',
    InvStatus:'',
    InvAmount:null
  }
  }
  onSubmit(form : NgForm){
    this.customerInvSer.PostCustomer_Invoices(form.value)
    .subscribe(res=>{
      this.resetForm();
      this.toaster.success('Succeed', 'add');
   })
  }

  //  getDate() {
  //   this.dateserService.viewCustomerInvoices().subscribe(res =>
  //     { this.allInvoices = res;
  //      console.log(res);
  //     })

  //  }

}
