import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { CustomerInvoicesService } from 'src/app/shared/customer-invoices.service';

@Component({
  selector: 'app-customer-invoice-add',
  templateUrl: './customer-invoice-add.component.html',
  styleUrls: ['./customer-invoice-add.component.css']
})
export class CustomerInvoiceAddComponent  implements OnInit{

  customerInvoices: any;
  constructor(public customerInvSer: CustomerInvoicesService) { }

  ngOnInit() {
    this.resetForm();
    this.getAllInvoices();
  }

  resetForm(form?: NgForm) {
    if (form != null)
      form.reset();
    this.customerInvSer.selectedCustomerInvoices = {
      InvIdN: null,
      InvId: '',
      InvDate: '',
      InvStatus: '',
      InvAmount: null
    }
  }

  getAllInvoices() {
    this.customerInvSer.getcustomerInvoicesList().subscribe(res =>
      this.customerInvoices = res
    )
  }
  onSubmit(form: NgForm) {
    if(form.value.InvIdN==null){
    this.customerInvSer.PostCustomer_Invoices(form.value)
      .subscribe(res => {
        this.resetForm();
        console.log("Succeed");
        this.getAllInvoices();
        this.customerInvSer.getcustomerInvoicesList();
        //this.toaster.success('Succeed', 'add');
      })}
      else{
        //update
        this.customerInvSer.PutCustomer_Invoices(form.value.InvId,form.value)
        .subscribe(res => {
         // this.getAllInvoices();
          this.customerInvSer.getcustomerInvoicesList();
        })
      }
  }

}
