import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { CustomerInvoicesService } from '../../shared/customer-invoices.service'
import { ToastrService } from 'ngx-toastr'
@Component({
  selector: 'app-customer-invoices',
  templateUrl: './customer-invoices.component.html',
  styleUrls: ['./customer-invoices.component.css'],
})
export class CustomerInvoicesComponent implements OnInit {

  customerInvoices: any;
  constructor(public customerInvSer: CustomerInvoicesService, private toaster: ToastrService) { }

  ngOnInit() {
    this.resetForm();
    this.getAllInvoices();
  }
  resetForm(form?: NgForm) {
    if (form != null)
      form.reset();
    this.customerInvSer.selectedCustomerInvoices = {
      InvId: '',
      InvDate: '',
      InvStatus: '',
      InvAmount: null
    }
  }

  getAllInvoices() {
    this.customerInvSer.getcustomerInvoicesList().subscribe(res =>
      this.customerInvoices = res
    )
  }
  onSubmit(form: NgForm) {
    this.customerInvSer.PostCustomer_Invoices(form.value)
      .subscribe(res => {
        this.resetForm();
        console.log("Succeed");
        this.getAllInvoices();
        //this.toaster.success('Succeed', 'add');
      })
  }
}
<form class="emp-form" #customerInvoicesForm="ngForm" (ngSubmit)="onSubmit(customerInvoicesForm)">
<div class="form-row">
    <div class="form-group col-md-6">
        <input class="forn-cntrol" name="InvId" #InvId="ngModel"  [(ngModel)]="customerInvSer.selectedCustomerInvoices.InvId" placeholder="InvID">
    </div>
    <div class="form-group col-md-6">
        <input  type="date"  class="forn-cntrol" name="InvDate"  #InvDate="ngModel" [(ngModel)]="customerInvSer.selectedCustomerInvoices.InvDate" placeholder="InvDate">
    </div>
    <div class="form-group col-md-6">
        <input class="forn-cntrol" name="InvStatus" #InvStatus="ngModel"  [(ngModel)]="customerInvSer.selectedCustomerInvoices.InvStatus" placeholder="InvStatus">
    </div>
    <div class="form-group col-md-6">
        <input type="number" class="forn-cntrol" name="InvAmount" #InvAmount="ngModel"  [(ngModel)]="customerInvSer.selectedCustomerInvoices.InvAmount" placeholder="InvAmount">
    </div>

    <div class="form-group col-md-8">
<button  type="submit" class="btn btn-lg btn-block btn-info"><i class="fa fa-floppy-o"></i>Add</button>   
 </div>
    <div class="form-group col-md-4">
        <button type="button" class="btn btn-lg btn-block btn-secondary" (click)="resetForm(customerInvoicesForm)"><i class="fa fa-repeat "></i>RESET</button>   
    </div>

</div>
</form>

<app-customer-invoices-list [customerInvoices]="customerInvoices"></app-customer-invoices-list>
