import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { CustomerInvoicesService } from '../../shared/customer-invoices.service'

@Component({
  selector: 'app-customer-invoices',
  templateUrl: './customer-invoices.component.html',
  styleUrls: ['./customer-invoices.component.css'],
})
export class CustomerInvoicesComponent implements OnInit{

  constructor(public customerInvSer:CustomerInvoicesService){ }

  ngOnInit() {
    this.resetForm();
  }
  resetForm(form?: NgForm){
    if(form!=null)
      form.reset();
    this.customerInvSer.selectedCustomerInvoices={
    InvId: '',
    InvDate:'',
    InvStatus:'',
    InvAmount:null
}
  }
}
