import { DatePipe } from '@angular/common';
import { Component, Input, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { CustomerInvoices } from 'src/app/shared/customer-invoices.model';
import { CustomerInvoicesService } from '../../Core/services/customer-invoices.service'

@Component({
  selector: 'app-customer-invoices-list',
  templateUrl: './customer-invoices-list.component.html',
  styleUrls: ['./customer-invoices-list.component.css']
})
export class CustomerInvoicesListComponent implements OnInit {

  //customerInvoicesList: any;
  //@Input() customerInvoices :any;
  customerInvoices :any;

  constructor(public customerInvSer: CustomerInvoicesService,   public datepipe: DatePipe,private router: Router) { }


  ngOnInit() {
    this.getAllInvoices();
    //this.datepipe.transform(this.customerInvoices.InvDate, 'dd/MM/yyyy')

  }

  getAllInvoices() {
    this.customerInvSer.getcustomerInvoicesList().subscribe(res =>
      this.customerInvoices = res
    )
  }
  showForEdit(customerInv: CustomerInvoices){
   this.customerInvSer.selectedCustomerInvoices.InvId=customerInv.InvId;

   // this.customerInvSer.selectedCustomerInvoices=Object.assign({}, customerInv);
    this.router.navigate(['/add-customerInv']);

  }
